import React, { useState } from "react";
import "./NavBar.css";
import { Link, NavLink } from "react-router-dom";

const NavBar = ({ columns, onAddCard }) => {
  const [showModal, setShowModal] = useState(false);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [subtasks, setSubtasks] = useState("");
  const [selectedColumn, setSelectedColumn] = useState("");

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleAddCard = () => {
    const newCard = {
      title,
      description,
      subtasks: subtasks.split(",").map(subtask => subtask.trim()),
      column: selectedColumn,
    };

    onAddCard(newCard);

    // Reset input fields
    setTitle("");
    setDescription("");
    setSubtasks("");
    setSelectedColumn("");
    setShowModal(false);
  };

  return (
    <NavLink className="navbar bg-body-tertiary ">
      <div className="container">
        <Link className="navbar-brand" to="/">
          Board
        </Link>
        <button className="btn btn-outline-success me-2" type="button" onClick={handleShowModal}>
          Add Card
        </button>
      </div>

      {/* Modal for adding new card */}
      <div className={`modal ${showModal ? "show" : ""}`} tabIndex="-1" role="dialog">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Add New Card</h5>
              <button type="button" className="close" onClick={handleCloseModal}>
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label htmlFor="cardTitle">Title</label>
                <input
                  type="text"
                  className="form-control"
                  id="cardTitle"
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="cardDescription">Description</label>
                <textarea
                  className="form-control"
                  id="cardDescription"
                  rows="3"
                  value={description}
                  onChange={e => setDescription(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="cardSubtasks">Subtasks (comma-separated)</label>
                <input
                  type="text"
                  className="form-control"
                  id="cardSubtasks"
                  value={subtasks}
                  onChange={e => setSubtasks(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label htmlFor="cardColumn">Column</label>
                <select
                  className="form-control"
                  id="cardColumn"
                  value={selectedColumn}
                  onChange={e => setSelectedColumn(e.target.value)}
                >
                  <option value="">Select Column</option>
                  {columns.map(column => (
                    <option key={column} value={column}>
                      {column}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                Close
              </button>
              <button type="button" className="btn btn-primary" onClick={handleAddCard}>
                Add Card
              </button>
            </div>
          </div>
        </div>
      </div>
    </NavLink>
  );
};

export default NavBar;
