import axios from "axios";
import React, { useState } from "react";

const PopupCard = ({ onClose }) => {
  const [title, setTitle] = useState();
  const [description, setDescription] = useState();
  const [subTask, setSubTask] = useState();
  const [status, setStatus] = useState();
  
//   const addCard = axios.post("http://127.0.0.1:8080/column/add-column", {
//     title: title,
//     description: description,
//     subTasks: subTask,
//     status: status,
//   })
//   .then((response) => {
   
//   })
//   .catch((err) => {
//     console.log("Error adding column:", err);
//   });





  const handelTitle = (e) => {
    setTitle(e.target.value);
    
  };
  const handelDescription = (e) => {
    setDescription(e.target.value);
    
  };
  const handelSubtask = (e) => {
    setSubTask(e.target.value);
    
  };
  const handelStatus = (e) => {
    setStatus(e.target.value);
    
  };

  return (
    <div className="popup-overlay">
      <div className="popup">
        <button className="close-button" onClick={onClose}>
          X
        </button>
        <div className="container mt-5">
          <form>
            <div className="mb-3">
              <label for="title" className="form-label">
                Title
              </label>
              <input
                onChange={handelTitle}
                value={title}
                type="text"
                className="form-control"
                id="title"
                name="title"
                placeholder="Enter title"
                required
              />
            </div>
            <div className="mb-3">
              <label for="description" className="form-label">
                Description
              </label>
              <textarea
               onChange={handelDescription}
               value={description}
                className="form-control"
                id="description"
                name="description"
                rows="3"
                placeholder="Enter description"
                required
              ></textarea>
            </div>
            <div className="mb-3">
              <label for="subtasks" className="form-label">
                Subtasks
              </label>
              <textarea
               onChange={handelSubtask}
               value={subTask}
                className="form-control"
                id="subtasks"
                name="subtasks"
                rows="3"
                placeholder="Enter subtasks (comma-separated)"
              ></textarea>
            </div>
            <div className="mb-3">
              <label for="status" className="form-label">
                Status
              </label>
              <select
                             
              className="form-select" onChange={handelStatus} value={status} id="status" name="status">
                <option value="Not Started">Not Started</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PopupCard;
