import React, { useContext, useState } from "react";
import Column from "../column/Column";
import ColumnPopup from "../column/Popup/Popup";
import { providerContext } from "../../Context/DataContext";

const Board = () => {
  const [isPopupVisible, setIsPopupVisible] = useState(false);
  const { data } = useContext(providerContext);

  const handleClick = () => {
    setIsPopupVisible(true);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-lg-9 col-md-9 scrolling-column">
          <div className="row">
            {data.map((value, index) => (
              <div className="col-lg-4 col-md-4" key={index}>
                <Column colValue={value.name} />
              </div>
            ))}
          </div>
        </div>
        <div className="col-lg-3 col-md-3 full-height">
          <div className="add-column-button">
            <button className="btn btn-primary" onClick={handleClick}>
              Add Column
            </button>
          </div>
        </div>
      </div>
      {isPopupVisible && <ColumnPopup onClose={() => setIsPopupVisible(false)} />}
    </div>
  );
};

export default Board;
