import axios from "axios";
import React, { useState } from "react";

const PopupCard = ({ onClose }) => {
  const [title, setTitle] = useState();
  const [description, setDescription] = useState();
  const [subTask, setSubTask] = useState();
  const [status, setStatus] = useState();
  
//   const addCard = axios.post("http://127.0.0.1:8080/column/add-column", {
//     title: title,
//     description: description,
//     subTasks: subTask,
//     status: status,
//   })
//   .then((response) => {
   
//   })
//   .catch((err) => {
//     console.log("Error adding column:", err);
//   });




  const handelInputsValue = (e) => {
    setTitle(e.target.value);
    setDescription(e.target.value);
    setSubTask(e.target.value);
    setStatus(e.target.value);
  };

  return (
    <div className="popup-overlay">
      <div className="popup">
        <button className="close-button" onClick={onClose}>
          X
        </button>
        <div class="container mt-5">
          <form>
            <div class="mb-3">
              <label for="title" class="form-label">
                Title
              </label>
              <input
                onChange={handelInputsValue}
                value={title}
                type="text"
                class="form-control"
                id="title"
                name="title"
                placeholder="Enter title"
                required
              />
            </div>
            <div class="mb-3">
              <label for="description" class="form-label">
                Description
              </label>
              <textarea
               onChange={handelInputsValue}
               value={description}
                class="form-control"
                id="description"
                name="description"
                rows="3"
                placeholder="Enter description"
                required
              ></textarea>
            </div>
            <div class="mb-3">
              <label for="subtasks" class="form-label">
                Subtasks
              </label>
              <textarea
               onChange={handelInputsValue}
               value={subTask}
                class="form-control"
                id="subtasks"
                name="subtasks"
                rows="3"
                placeholder="Enter subtasks (comma-separated)"
              ></textarea>
            </div>
            <div class="mb-3">
              <label for="status" class="form-label">
                Status
              </label>
              <select
                             
              class="form-select" id="status" name="status">
                <option value="not_started">Not Started</option>
                <option value="in_progress">In Progress</option>
                <option value="completed">Completed</option>
              </select>
            </div>
            <button type="submit" class="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default PopupCard;
