  //     .then((response) => {
  //       setSuccessfully(response.data);
  //       // console.log(response.data);
  //     })
  //     .catch((err) => {
  //       console.log("Error adding column:", err);
  //     });

  //   console.log("New Card Data:", newCard);
  //   // setPopupOpen(false);
  // };

  return (
    <>
      <div className="card back__ground__card" onClick={openPopup}>
        <div className="card-body">
          <h5 className="card-title">{cardValue.title}</h5>
          <p className="card-text">(0 of 3) SubTasks</p>
        </div>
      </div>
      {isPopupOpen && <CardInfo id={cardValue._id} onClose={closePopup} />}
    </>
  );
};

export default Card;
