import React from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const options = {
  responsive: true,
  plugins: {
    legend: {
      position: "top",
    },
    title: {
      display: true,
      text: "Chart.js Bar Chart",
    },
  },
};

const labels = ["January", "February", "March", "April", "May", "June", "July"];

const arr = [100, 200, 300, 400, 500, 600, 700];
const DataChart = () => {
  const { data } = useContext(providerContext);
   




  const dataChart = {
    labels,
    datasets: [
      {
        label: "Dataset 1",
        data: arr,
        backgroundColor: "rgba(255, 99, 132, 0.5)",
      },
    ],
  };
  return (
    <>
    <div className="w-50">
        {React.createElement(Bar, { options: options, data: dataChart })}
    </div>
    </>
  )
};

export default DataChart;
