// SideBar.js
import React, { useState } from "react";

const SideBar = () => {
  const [activeButton, setActiveButton] = useState(null);

  const toggleSidebar = (buttonIndex) => {
    setActiveButton(buttonIndex === activeButton ? null : buttonIndex);
  };

  return (
    <div className={`w-25 bg-dark sidebar ${activeButton ? "active" : "inactive"}`}>
      <div className="list-group">
        <button
          type="button"
          className={`list-group-item list-group-item-action ${
            activeButton === 1 ? "active" : ""
          }`}
          onClick={() => toggleSidebar(1)}
        >
          The current button
        </button>
        <button
          type="button"
          className={`list-group-item list-group-item-action ${
            activeButton === 2 ? "active" : ""
          }`}
          onClick={() => toggleSidebar(2)}
        >
          A second button item
        </button>
        <button
          type="button"
          className={`list-group-item list-group-item-action ${
            activeButton === 3 ? "active" : ""
          }`}
          onClick={() => toggleSidebar(3)}
        >
          A third button item
        </button>
      </div>
    </div>
  );
};

export default SideBar;
