import React, { useContext, useState } from "react";
import "./NavBar.css";
import { Link, NavLink } from "react-router-dom";
import PopupCard from "./PopupCard";
import { providerContext } from "../../Context/DataContext";
const NavBar = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const openPopup = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };
  const {data} = useContext(providerContext)
  return (
    <div>
      <NavLink className="navbar bg-body-tertiary">
        <div className="container">
          <Link className="navbar-brand ms-5" to="/">
            Board
          </Link>
          <button className="btn btn-outline me-2" type="button" onClick={openPopup}>
            Add New Card
          </button>
        </div>
      </NavLink>
      {isPopupOpen && <PopupCard data={data} onClose={closePopup} />}
    </div>
  );
};

export default NavBar;
