import React, { useContext, useState } from "react";
import "./EditPopup.css"; // Import the stylesheet
import { providerContext } from "../../../Context/DataContext";
import axios from "axios";

const EditPopup = ({ onClose, onSave, id }) => {
  const [title, setTitle] = useState();
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("To Do");
  //  const [successfully, setSuccessfully] = useState("")
  const [value, setValue] = useState("");
  const { data, cards } = useContext(providerContext);
  const selectedCard = cards.filter((card) => card._id.includes(id));
//   console.log("cards", selectedCard[0].title);
  const Id = selectedCard[0]._id;
  const handleSave = () => {
      setValue(selectedCard[0].status)
      onSave({ title, description, status }, Id);
      onClose();
    };
    
    //   const editcard = () => {
        //     axios
  //       .patch(`http://127.0.0.1:8080/edit-card/${Id}`, {
  //         title: title,
  //         description: description,
  //         status: status,
  //       })
  //       .then((response) => {
  //         // setSuccessfully(response)
  //         console.log(response)
  //       })
  //       .catch((err) => {
  //         console.log("Error adding column:", err);
  //       });
  //   };

  return (
    <div className="popup-container">
      <div className="popup">
        <div className="popup-content">
          <h2>Create New Card</h2>
          <div className="form-group">
            <label>Title:</label>
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder={selectedCard[0].title}
            />
          </div>
          <div className="form-group">
            <label>Description:</label>
            <textarea
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder={selectedCard[0].description}
            />
          </div>
          <div className="form-group">
            <label>Status:</label>
            <select
              className="form-control"
              value={status}
              onChange={(e) => setStatus(e.target.value)}
            >
              <option value={value}>Choose</option>
              {data.map((opt, index) => {
                return (
                  <option key={index} value={opt.name}>
                    {opt.name}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <div className="popup-buttons">
          <button className="btn btn-primary" onClick={handleSave}>
            Save
          </button>
          <button className="btn btn-secondary" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditPopup;
