import React, { useState, useEffect } from "react";
import "./NavBar.css";
import { Link, NavLink } from "react-router-dom";
import axios from "axios";

const NavBar = ({ onAddCard }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [subtasks, setSubtasks] = useState("");
  const [selectedColumn, setSelectedColumn] = useState("");

  useEffect(() => {
    // Fetch columns using Axios
    axios.get("your_column_data_url")
      .then(response => {
        setColumns(response.data.columns);
      })
      .catch(error => {
        console.error("Error fetching columns:", error);
      });
  }, []);

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleAddCard = () => {
    const newCard = {
      title,
      description,
      subtasks: subtasks.split(",").map(subtask => subtask.trim()),
      column: selectedColumn,
    };

    onAddCard(newCard);

    setTitle("");
    setDescription("");
    setSubtasks("");
    setSelectedColumn("");
    setShowModal(false);
  };

  return (
    <NavLink className="navbar bg-body-tertiary ">
      <div className="container">
        <Link className="navbar-brand" to="/">
          Board
        </Link>
        <button className="btn btn-outline-success me-2" type="button" onClick={handleShowModal}>
          Add Card
        </button>
      </div>

      {/* Modal for adding new card */}
    
    </NavLink>
  );
};

export default NavBar;
