// SideBar.js
import React, { useState } from "react";
import "./SideBar.css"; // You can create this CSS file for your custom styles
import boardImg from "../../image/board.svg";
const SideBar = () => {
  const [activeButton, setActiveButton] = useState(null);

  const toggleSidebar = (buttonIndex) => {
    setActiveButton(buttonIndex === activeButton ? null : buttonIndex);
  };

  return (
    <div
      className={`w-25 bg-black sidebar ${
        activeButton ? "active" : "inactive"
      }`}
      style={{ height: "100vh" }}
    >
      <div className="list-group">
        <button
          type="button"
          className={`list-group-item list-group-item-action ${
            activeButton === 1 ? "active" : ""
          }`}
          onClick={() => toggleSidebar(1)}
        >
          <img src={boardImg} alt="add new board icon" />
          <span>Platform Launch</span>
        </button>
        <button
          type="button"
          className={`list-group-item list-group-item-action ${
            activeButton === 2 ? "active" : ""
          }`}
          onClick={() => toggleSidebar(2)}
        >
          <img src={boardImg} alt="add new board icon" />
          <span>Marketing Plan</span>
        </button>
        <button
          type="button"
          className={`list-group-item list-group-item-action ${
            activeButton === 3 ? "active" : ""
          }`}
          onClick={() => toggleSidebar(3)}
        >
          <img src={boardImg} alt="add new board icon" />
          third
        </button>
      </div>
    </div>
  );
};

export default SideBar;
