import React, { useContext, useState } from "react";
import Column from "../column/Column";

import { providerContext } from "../../Context/DataContext";
import "./Board.css"; // Import your custom CSS file


const Board = () => {



  const { data } = useContext(providerContext);

  return (
    <div className="container-fluid ">
      <div className="row flex-nowrap overflow-auto custom-scrollbar h-100">
        {data.map((value, index) => (
          <div className="col-lg-4 col-md-4 card m-3 column__style " key={index}>
            <Column colValue={value.name} />
          </div>
        ))}

        <div className="col-lg-2 col-md-4 d-flex align-items-center  m-4 ">
          <div className="card add-column-card h-100 column__style">
            <div className="card-body d-flex align-items-center justify-content-center">
              <button
                className="btn btn__color btn-block"
                onClick={openPopup}
              >
                Add Column
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Board;
