  //         // console.log(filterCard);
  //       })
  //       .catch((err) => {
  //         console.log("Error adding column:", err);
  //       });
  //   });
  const { cards, data } = useContext(providerContext);
  const columnName = data.map((value) => value.name);
  // console.log("col ", columnName);
  // console.log("car  ", cards);
  const filterCard = cards.filter((card) => columnName.includes(card.status));
  // console.log(" adfad", filterCard);
  return (
    <>
      <div className="container bg-primary">
        <div>{colValue}</div>
        {filterCard.map((value, index) => {
          return <Card cardValue={value} key={index} />;
        })}
      </div>
    </>
  );
};

export default Column;
